import React, {useMemo} from 'react';
import {NavLink} from '@remix-run/react';
import {useAside} from '~/components/Aside';

export default function MobileBottomNavigation({cart}) {
  const {open} = useAside();

  /* ---------------------------------------------------------------
     Derive quantity from ANY cart shape
  ----------------------------------------------------------------*/
  const itemCount = useMemo(() => {
    if (!cart) return 0;

    // 1) Plain numeric field (Hydrogen’s recommended field)
    if (typeof cart.totalQuantity === 'number') {
      return cart.totalQuantity;
    }

    // 2) Flat lines array {lines: [{quantity: 2}, …]}
    if (Array.isArray(cart.lines)) {
      return cart.lines.reduce((sum, l) => sum + (l.quantity ?? 0), 0);
    }

    // 3) Relay-style {lines: {edges: [{node: {quantity}}]}}
    if (Array.isArray(cart.lines?.edges)) {
      return cart.lines.edges.reduce(
        (sum, edge) => sum + (edge?.node?.quantity ?? 0),
        0,
      );
    }

    // 4) Last-ditch: Shopify Storefront API draft {items: […]}
    if (Array.isArray(cart.items)) {
      return cart.items.reduce((sum, i) => sum + (i.quantity ?? 0), 0);
    }

    return 0;
  }, [cart]);

  return (
    <nav className="mobile-bottom-nav">
      <NavLink
        prefetch="intent"
        to="/"
        className="nav-item"
        aria-label="Homepage Button"
        end
      >
        <HomeIcon />
      </NavLink>

      <NavLink
        prefetch="intent"
        to="/collections"
        aria-label="All Collections Button"
        className="nav-item"
      >
        <CollectionsIcon />
      </NavLink>

      <NavLink
        prefetch="intent"
        to="/account"
        className="nav-item"
        aria-label="User Account Button"
      >
        <UserIcon />
      </NavLink>

      <button
        className="nav-item cart-toggle"
        aria-label="Open Cart"
        onClick={() => open('cart')}
      >
        <CartIcon />
        {itemCount > 0 && <span className="badge">{itemCount}</span>}
      </button>
      <NavLink
        prefetch="intent"
        to="https://wa.me/96171888036"
        className="nav-item"
        target="_blank"
        aria-label="Contact is on Whatsapp"
      >
        <WhatsappIcon />
      </NavLink>
    </nav>
  );
}

